mapper.select:
SELECT o.id as organizationid, o.organizationname, o.organizationtype, group_concat( DISTINCT ( CASE WHEN oa.authoritytypeflag = 0 THEN '用户及组管理' WHEN oa.authoritytypeflag = 1 THEN '产品管理' WHEN oa.authoritytypeflag = 2 THEN '借款人管理' WHEN oa.authoritytypeflag = 3 THEN '申请单审批' WHEN oa.authoritytypeflag = 4 THEN '借据维护' WHEN oa.authoritytypeflag = 5 THEN '综合查询统计' WHEN oa.authoritytypeflag = 6 THEN '权限配置' WHEN oa.authoritytypeflag = 7 THEN '客户信息导出' ELSE oa.authoritytypeflag END ) SEPARATOR ';' ) AS authoritytypeflags FROM organization o LEFT JOIN organizationauthority oa ON o.id = oa.organizationid
and o.organizationname = #{organizationname,jdbcType=VARCHAR}
and o.organizationtype = #{organizationtype,jdbcType=VARCHAR}
group by oa.organizationid
